    let last := mload(sEnd)
    for { } lt(source, sEnd) { }
    {
        mstore(dest, mload(source))
        source := add(source, 32)
        dest := add(dest, 32)
    }
    mstore(dEnd, last)
}

{
    length := sub(length, 32)
    let sEnd := add(source, length)
    let dEnd := add(dest, length)
    let first := mload(source)
    for { } slt(dest, dEnd) { }
