attributes.operations:
{
    let ptr := mload(0x40)
    let success := call(gas(), _addr, 0, add(_calldata, 0x20), mload(_calldata), ptr, 0x20)
    if gt(success, 0)
    {
        switch returndatasize()
        case 0 {
            ret := 1
        }
        case 0x20 {
            ret := eq(mload(ptr), 1)
        }
        default {
        }
    }
}
